                        items = solicitudes.OrderBy(x => x.CodigoZona);
                        break;
                    case "Seccion":
                        items = solicitudes.OrderBy(x => x.CodigoSeccion);
                        break;
                    case "Territorio":
                        items = solicitudes.OrderBy(x => x.CodigoTerritorio);
                        break;
                    case "Direccion":
                        items = solicitudes.OrderBy(x => x.Direccion);
